layout.dados:
select c.*, p.preco_custo,
	(select quantidade from lancto where produto=c.produto and operacao='M'
		and data<=$data order by data desc limit 1) as estoque
from (
select l.produto, p.nome, sum(l.quantidade) as quantidade, sum(l.valor) as valor
from lancto l join produto p on (l.produto=p.grid)
where p.tipo='C' and l.data=$data
group by l.produto, p.nome
) c join produto p on (c.produto=p.grid)
